hudson.tasks.Shell.command:
## Publish the production Integration Stack update sites, target platform repositories, offline zip files and discovery ## XML/ composite site/ plugin jars to the designated BUILD_TYPE/updates area. ## e.g. - https://devstudio.redhat.com/11/development/updates/integration-stack/
DEVSTUDIO_ROOT=devstudio@filemgmt.jboss.org:/www_htdocs/devstudio
## Create a local copy of the bits to be promoted. rsync -aPrzq --protocol=28 ${DEVSTUDIO_ROOT}/${SOURCE_PATH}/${VERSION}/* ${WORKSPACE}/devstudiois-publish rsync -aPrzq --protocol=28 ${DEVSTUDIO_ROOT}/${SOURCE_PATH}/discovery/${VERSION}/* ${WORKSPACE}/devstudiois-publish/discovery rsync -aPrzq --protocol=28 ${DEVSTUDIO_ROOT}/${SOURCE_PATH}/${VERSION}/* ${WORKSPACE}/devstudiois-publish/
### Promote the release.
# Promote the released components update site (including installers), target platform and discovery
# https://devstudio.redhat.com/11/development/updates/integration-stack/${VERSION}/ . ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish \ -t ${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/${VERSION} -DESTINATION ${DEVSTUDIO_ROOT}
# Promote the released components target platform - # https://devstudio.redhat.com/11/stable/updates/integration-stack/${VERSION}/targetplatform/ #. ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/targetplatform \ # -t ${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/${VERSION}/targetplatform -DESTINATION ${DEVSTUDIO_ROOT}
# Promote the Discovery composite site. . ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/discovery/ \ -t ${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/discovery/ -DESTINATION ${DEVSTUDIO_ROOT}
. ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/discovery/ \ -t ${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/discovery/${VERSION}/ -DESTINATION ${DEVSTUDIO_ROOT}
## Promote the early access components update site, target platform and discovery if [[ ${EA_ENABLED} == "true" ]]; then
# https://devstudio.redhat.com/11/stable/updates/integration-stack/${VERSION}/earlyaccess/ . ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/earlyaccess \ -t ${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/${VERSION}/earlyaccess -DESTINATION ${DEVSTUDIO_ROOT}
# Promote the early access components target platform - # https://devstudio.redhat.com/11/development/updates/integration-stack/${VERSION}/earlyaccess/targetplatform/ . ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/earlyaccess/targetplatform \ -t ${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/${VERSION}/earlyaccess/targetplatform -DESTINATION ${DEVSTUDIO_ROOT}
# Discovery early access composite site. . ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/discovery/earlyaccess/ \ -t ${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/discovery/earlyaccess/ -DESTINATION ${DEVSTUDIO_ROOT}
. ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/discovery/earlyaccess/ \ -t ${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/discovery/${VERSION}/earlyaccess/ -DESTINATION ${DEVSTUDIO_ROOT} fi
## Mirror the release to static
## Promote the released components update site (including installers), target platform and discovery
# https://devstudio.redhat.com/static/11/development/updates/integration-stack/${VERSION}/ ###. ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish \ ### -t static/${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/${VERSION} -DESTINATION ${DEVSTUDIO_ROOT}
# Promote the released components target platform - # https://devstudio.redhat.com/static/11/stable/updates/integration-stack/${VERSION}/targetplatform/ #. ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/targetplatform \ # -t static/${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/${VERSION}/targetplatform -DESTINATION ${DEVSTUDIO_ROOT}
# Promote the JBoss Central discovery sites and meta-data.
# Discovery composite site. . ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/discovery/ \ -t static/${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/discovery/${VERSION}/ -DESTINATION ${DEVSTUDIO_ROOT}
## Promote the early access components update site, target platform and discovery if [[ ${EA_ENABLED} == "true" ]]; then
# Promote the early access components update site - # https://devstudio.redhat.com/static/11/stable/updates/integration-stack/${VERSION}/earlyaccess/ . ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/earlyaccess \ -t static/${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/${VERSION}/earlyaccess -DESTINATION ${DEVSTUDIO_ROOT}
# Promote the early access components target platform - # https://devstudio.redhat.com/static/11/development/updates/integration-stack/${VERSION}/earlyaccess/targetplatform/ . ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/earlyaccess/targetplatform \ -t static/${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/${VERSION}/earlyaccess/targetplatform -DESTINATION ${DEVSTUDIO_ROOT}
# Discovery early access composite site. . ${WORKSPACE}/sources/publish/rsync.sh -s ${WORKSPACE}/devstudiois-publish/discovery/earlyaccess/ \ -t static/${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/discovery/${VERSION}/earlyaccess/ -DESTINATION ${DEVSTUDIO_ROOT} fi
### Promote the update site zips
export SOURCE_PATH=11/staging/updates/integration-stack export ZIP_DESTINATION=${DEVSTUDIO_ROOT}/${VERSION_MAJOR}/${BUILD_TYPE}/updates/${PARENT_FOLDER}/${VERSION}/
# Promote the released update site zip. #rsync -aPrzq --protocol=28 ${DEVSTUDIO_ROOT}/${SOURCE_PATH}/devstudio-integration-stack-${VERSION}-updatesite.zip \ # ${WORKSPACE}/devstudiois-publish/devstudio-integration-stack-${VERSION}-updatesite.zip #rsync -aPrzq --protocol=28 ${DEVSTUDIO_ROOT}/${SOURCE_PATH}/devstudio-integration-stack-${VERSION}-updatesite.zip.sha256 \ # ${WORKSPACE}/devstudiois-publish/devstudio-integration-stack-${VERSION}-updatesite.zip.sha256 #rsync -aPrzq --protocol=28 ${WORKSPACE}/devstudiois-publish/devstudio-integration-stack-${VERSION}-updatesite.zip \ # ${ZIP_DESTINATION}/devstudio-integration-stack-${VERSION}-updatesite.zip #rsync -aPrzq --protocol=28 ${WORKSPACE}/devstudiois-publish/devstudio-integration-stack-${VERSION}-updatesite.zip.sha256 \ # ${ZIP_DESTINATION}/devstudio-integration-stack-${VERSION}-updatesite.zip.sha256
# Promote the EA update site zip if [[ ${EA_ENABLED} == "true" ]]; then rsync -aPrzq --protocol=28 ${DEVSTUDIO_ROOT}/${SOURCE_PATH}/devstudio-integration-stack-${VERSION}-updatesite-earlyaccess.zip \ ${WORKSPACE}/devstudiois-publish/devstudio-integration-stack-${VERSION}-updatesite-earlyaccess.zip rsync -aPrzq --protocol=28 ${DEVSTUDIO_ROOT}/${SOURCE_PATH}/devstudio-integration-stack-${VERSION}-updatesite-earlyaccess.zip.sha256 \ ${WORKSPACE}/devstudiois-publish/devstudio-integration-stack-${VERSION}-updatesite-earlyaccess.zip.sha256 rsync -aPrzq --protocol=28 ${WORKSPACE}/devstudiois-publish/devstudio-integration-stack-${VERSION}-updatesite-earlyaccess.zip \ ${ZIP_DESTINATION}/devstudio-integration-stack-${VERSION}-updatesite-earlyaccess.zip rsync -aPrzq --protocol=28 ${WORKSPACE}/devstudiois-publish/devstudio-integration-stack-${VERSION}-updatesite-earlyaccess.zip.sha256 \ ${ZIP_DESTINATION}/devstudio-integration-stack-${VERSION}-updatesite-earlyaccess.zip.sha256
fi
